    $(document).ready(function () {
		
		$('.menuNavigation > input').click(function(eventObject) {
			eventObject.preventDefault();
		});
		
		
		$('.menuCategory').hide();
		$('.menuNavigation > input:first').toggle(function() {
			$('.menuCategory').slideDown();
		}, function(){
			$('.menuCategory').slideUp();		
		
		});
	});
	
	