    $(document).ready(function () {
		
		$('.menuNavigation > input').click(function(eventObject) {
			eventObject.preventDefault();
		});
		
		
		$('.menuCategory').hide();
		/*$('.menuNavigation > input:first').toggle(function() {
			$('.menuCategory').slideDown();
		}, function(){
			$('.menuCategory').slideUp();		
		
		});*/
		
		$('.menuNavigation > input:first').hover(function() {
			$('.menuCategory').fadeIn();
			$('.menuNavigation > input:first').css({
			'background-color': '#FFCEC9',
			'border': '1px solid #EF5928'
			});
		});
		$('.menuNavigation > input:first').mouseleave(function() {
			$('.menuCategory').fadeOut();
			$('.menuNavigation > input:first').css({
			'background-color': '#fff',
			'border': '1px solid #EFEFEF'
			});			
		});
	});
	
	